const url = "{{base_url}}/pets";
const headers = {
  "Content-Type": "application/json"
};

const body = {
  "lives": 9,
  "name": "James Horton",
  "petType": "cat"
};
fetch(url, {
  method: "POST",
  headers,
  body: JSON.stringify(body)
});
